%Scaling data to [-1,1] for SVMlibfunction [varargout] = Scale(varargin)    mins = min(varargin{1}, [], 1);    ranges = max(varargin{1}, [], 1) - mins;    for i = 1:length(varargin)        varargout{i} = 2 * ((varargin{i} - repmat(mins, size(varargin{i}, 1), 1)) ...                            ./ repmat(ranges, size(varargin{i}, 1), 1)) - 1;        varargout{i}(:, ranges == 0) = 0;end